		<?php		//------------------------------------------------------------------------------		// Mailing List Unsubscription v 2.0		//		// (c) Andrew Collington - amnuts@talker.com		// (c) Michael Doig      - michael@coastart.net		// On behalf of Coastart Internet Services - all rights reserved.		//------------------------------------------------------------------------------		// start up the database class		$db = new mysql_class($dbcfg['login'],$dbcfg['pword'],$dbcfg['server']);		$db->SelectDB($dbcfg['db']);		// check for the id number		if (!isset($id) || $id=="") echo "<p>You must supply your id number when unsubscribing from the mailing list.</p>\n";		// check for email address		else if (!isset($email) || $email=="") echo "<p>You must supply your email address when unsubscribing from the mailing list.</p>\n";		// check for combination of both		else if (!$db->Exists("SELECT id FROM $tbcfg[mlemails] WHERE ID=$id AND email='$email'")) echo "<p>That email and id combination does not exist in the database.</p>\n";		// else everything is cool		else {			$db->Update("UPDATE $tbcfg[mlemails] SET unsubscribed=1 WHERE ID=$id");			if ($db->a_rows != -1) echo "<p>You have now unsubscribed yourself from the mailing list.</p>\n";			else echo "<p>You could not be removed from the mailing list at this time.</p>\n";		}		?>